        <?php if ($currentPage < $totalPages): ?>
            <a href="?page=<?php echo $currentPage + 1; ?>" style="padding-left:10px"> <i class="fas fa-chevron-right"></i> </a>
        <?php else: ?>
            <span class="disabled" style="padding-left:10px"> <i class="fas fa-chevron-right"></i> </span>
        <?php endif; ?>
    <?php endif; ?>
</div>

</div>


  </body>